package interview;

public class InterviewQuestions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i <=20;i++) {
			if (i%2 !=0) {
				System.out.println("odd"+i);}
				
			else if (i%2==0) {
			
			System.out.println("even"+i);	
			}
		
		}
		
		}
	}

